using System.Linq;
using Accord.Math;
using Munkres;
using Xunit;
using Xunit.Abstractions;

namespace JaroWinklerRecordSearch.Test
{
    public class MunkresTest
    {
        private readonly ITestOutputHelper _log;
        public MunkresTest(ITestOutputHelper log)
        {
            _log = log;
        }

        [Fact]
        public void minimize_test()
        {
            #region doc_example
            // This is the same example that is found in Wikipedia:
            // https://en.wikipedia.org/wiki/Hungarian_algorithm

            double[,] costMatrix =
            {
                //                      Clean bathroom, Sweep floors,  Wash windows
                /* Armond   */ {    2,           3,           3       },
                /* Francine */ {    3,           2,           3       },
                /* Herbert  */ {    3,           3,           2       },
            };

            // Create a new Hungarian algorithm
            var m = new MunkresProgram();
            m.RunMunkres(costMatrix);
            _log.WriteLine(m.Log.ToString());

            var solution = m.M; // Solution will be 0, 1, 2
            #endregion

            Assert.Equal(1, solution[0,0]);
            Assert.Equal(1, solution[1,1]);
            Assert.Equal(1, solution[2,2]);
        }

        [Fact]
        public void minimize_test2()
        {
            var costMatrix = Jagged.Magic(5).ToMatrix();

            var m = new MunkresProgram();
            m.RunMunkres(costMatrix);
            _log.WriteLine(m.Log.ToString());

            Assert.Equal(5, m.NRow);
            Assert.Equal(5, m.NCol);

            //Assert.Equal(15, m.Value);
            Assert.Equal(1, m.M[0,2]);
            Assert.Equal(1, m.M[1,1]);
            Assert.Equal(1, m.M[2,0]);
            Assert.Equal(1, m.M[3,4]);
            Assert.Equal(1, m.M[4,3]);
        }

        [Fact]
        public void minimize_test3()
        {
            // http://csclab.murraystate.edu/~bob.pilgrim/445/munkres.html

            double[,] costMatrix =
            {
                { 1, 2, 3 },
                { 2, 4, 6 },
                { 3, 6, 9 },
            };

            var m = new MunkresProgram();
            m.RunMunkres(costMatrix);
            _log.WriteLine(m.Log.ToString());

            Assert.Equal(3, m.NRow);
            Assert.Equal(3, m.NCol);

            //Assert.Equal(10, m.Value);
            Assert.Equal(1, m.M[0,2]);
            Assert.Equal(1, m.M[1,1]);
            Assert.Equal(1, m.M[2,0]);
        }

        [Fact]
        public void minimize_test_more_cols()
        {
            double[,] costMatrix =
            {
                { 1, 2, 3, 4  },
                { 2, 4, 6, 8  },
                { 3, 6, 9, 12 },
            };

            var m = new MunkresProgram();
            m.RunMunkres(costMatrix);
            _log.WriteLine(m.Log.ToString());

            Assert.Equal(3, m.NRow);
            Assert.Equal(4, m.NCol);

            //Assert.Equal(10, m.Value);
            Assert.Equal(1, m.M[0,2]);
            Assert.Equal(1, m.M[1,1]);
            Assert.Equal(1, m.M[2,0]);
        }

        [Fact]
        public void minimize_test_more_rows2()
        {
            double[,] costMatrix =
            {
                { 1, 2,  3 },
                { 2, 4,  6 },
                { 3, 6,  9 },
                { 4, 8, 12 },
            };

            var m = new MunkresProgram();
            m.RunMunkres(costMatrix);
            _log.WriteLine(m.Log.ToString());

            Assert.Equal(4, m.NRow);
            Assert.Equal(3, m.NCol);

            //Assert.Equal(10, m.Value);
            Assert.Equal(1, m.M[0,2]);
            Assert.Equal(1, m.M[1,1]);
            Assert.Equal(1, m.M[2,0]);
            Assert.Equal(0, m.M[3,0]);
            Assert.Equal(0, m.M[3,0]);
            Assert.Equal(0, m.M[3,0]);
        }

